import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import logo from './logo.svg';
import PostFeed from "./Post-feed";
import { useFetchWrapper } from "./_helpers";


export function Feed(location) {
  const [loaded, setLoaded] = useState(false);
  const [items, setItems] = useState([]);
  const fetchWrapper = useFetchWrapper();
  var page = 0;

  function likeContent(id){
    fetchWrapper.post(process.env.REACT_APP_API_URL+"/like", {"id":id});
  }

  function loadContent(page){
      fetchWrapper.get(process.env.REACT_APP_API_URL+"/feed?page="+page).then(result => {
        console.log(result);
      setItems(items.concat(result));
    });
  }
  window.addEventListener('scroll', function() {
    if ((window.innerHeight + window.scrollY) >= document.body.offsetHeight) {
       console.log("you're at the bottom of the page");
       page = 1
       console.log(page);
       loadContent(page);
    }
 });
 useEffect(() => {if(page != 0){document.getElementById("feed").innerHTML = items.map(item => PostFeed(item, likeContent))}}, items);
  useEffect(() => { setLoaded(true)}, [items]);
  if(!loaded){
    loadContent(page);
    return <div className="load">"Loading..."</div>;
  } else{
  return (
    <div id="feed" className="feed">
      {items.map(item => PostFeed(item, likeContent))}
    </div>
  )}
}
