import * as React from "react";
import { useState, useEffect } from "react";
import logo from './logo.svg';
import { useFetchWrapper } from "./_helpers";
import { useParams } from 'react-router-dom';
import Comment from './Comment';

function Post() {
  const fetchWrapper = useFetchWrapper();
  const { id } = useParams();
  const [item, setItem] = useState();
  const [loaded, setLoaded] = useState(false);
  var render = ""
  setItem(fetchWrapper.get(process.env.REACT_APP_API_URL+"/post/"+id));
  useEffect(() => { setLoaded(true)}, [item]);
  var comment = "";
  function likeContent(id){
    fetchWrapper.post(process.env.REACT_APP_API_URL+"/like", {"id":id})
  }
  function sub() {
    return fetchWrapper.post(process.env.REACT_APP_API_URL+"/makecomment", {"comment":comment, "id":item.id});
  }
  var comment = "";
  console.log(item);
  var isLikedContent = (<img className="likeContent" src="/img/nolike.svg"/>);
  var isLiked = false;

  function setLike(v){
    likeContent(v);
    if (isLiked == true){
      isLiked = false;
      isLikedContent = (<img className="likeContent" src="/img/nolike.svg"/>);
    } else{
      isLiked = true;
      isLikedContent = (<img className="likeContent" src="/img/liked.svg"/>);
    }
    return render;
  }
  if(!loaded){
      if(item.isliked){
      isLiked = true;
      isLikedContent = (<img className="likeContent" src="/img/liked.svg"/>);
    }
  var likeText = "likes";
  if(item.likes == 1){
    likeText = "like";
  }

    var render = (
      <div className="post">
        <div className="post-top"><div><a href={"/profile/"+item.poster}><img className = "post-profile-pic" src={process.env.REACT_APP_API_URL+item.postppic}></img></a></div><div>{item.poster} on <a href={"/bubble/"+item.community}>{item.community}</a></div></div>
        <div className = "ptext">
          <p>
              {item.content}</p>
            </div>
            <div className="image">
            <a href = {"/post/"+item.id}>
              <div class="post-image">
            <img className = "pimg" src={process.env.REACT_APP_API_URL+item.flocation}></img></div></a> 
        </div>
        <div className="bottom">
        <button className="cbut" onClick={e => setLike(item.id)}>{isLikedContent}</button>
          <a className="" href ={"/post/"+item.id}><img className="cbut likeContent" src="/img/comment.svg" /></a>

            <div className ="likes">
              {item.likes} {likeText}
            </div>

            <div className ="comments">
            </div>
        </div>
        <div className= "makeComment">
              <textarea className="mccomment" rows="1" placeholder="Make a comment..."></textarea>
            </div>
      </div>
    );
    return render;
  } else{

    return render;    
  }
  }


export default Post;
