import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import logo from './logo.svg';
import PostFeed from "./Post-feed";
import { useFetchWrapper } from "./_helpers";


export function Feed(location, page) {
  const [loaded, setLoaded] = useState(false);
  const [items, setItems] = useState([]);
  fetchWrapper = useFetchWrapper();

  function likeContent(id){
    fetchWrapper.post(process.env.REACT_APP_API_URL+"/like", {"id":id});
  }

  function loadContent(page){
      fetchWrapper.get(process.env.REACT_APP_API_URL+"/feed", {"page":page}).then(result => {
      setItems(result);
    });
  }

  loadContent(0);
  useEffect(() => { setLoaded(true)}, [items]);
  if(!loaded){
    return <div className="load">"Loading..."</div>;
  } else{
  return (
    <div className="feed">
      {this.state.itms.map(item => PostFeed(item, likeContent))}
    </div>
  )}
}
