import React, { useState } from "react";
import Home from "./Home";
import logo from './logo.svg';
import { useFetchWrapper } from "./_helpers";
function PostFeed(item, likeContent) {
  var comment = "";
  console.log(item);
  var isLikedContent = ""
  if(item.isliked){
      isLikedContent = "/img/liked.svg"
    }
  else{
    isLikedContent = "/img/nolike.svg"
  }
    return (
      <div className="post">
        <div className="post-top"><div><a href={"/profile/"+item.poster}><img className = "post-profile-pic" src={process.env.REACT_APP_API_URL+item.postppic}></img></a></div><div>{item.poster} on <a href={"/bubble/"+item.community}>{item.community}</a></div></div>
        <div className = "ptext">
          <p>
              {item.content}</p>
            </div>
            <div className="image">
            <img className = "post-image" src={process.env.REACT_APP_API_URL+item.flocation}></img>
        </div>
        <div className="bottom">
            <div className ="likes">
              {item.likes} likes
            </div>
            <div className ="comments">
            </div>
            <button className="cbut" onClick={e => likeContent(item.id)}><img className="likeContent" src={isLikedContent}/> </button>
            <a className="" href ={"/post/"+item.id}><img className="cbut likeContent" src="/img/comment.svg" /></a>
        </div>
      </div>
    );
  }

export default PostFeed;
