import React, { useState, Component} from "react";
import logo from './logo.svg';
import PostFeed from './Post-feed'
import { useFetchWrapper } from "./_helpers";

class Home extends Component{
  constructor(props){
    super(props);
    this.state ={
      loaded: false,
      itms: [],
    }
    this.likeContent = this.likeContent.bind(this);
    this.loadContent = this.loadContent.bind(this);
  }


  render(){
    this.loadContent(0);
    if(!this.state.loaded){
      return <div className="load">"Loading..."</div>;
    } else{
      return (
        <div className="centerPos">
        <div className="feed">

        </div>
        <div className="create">
        <div className="createpost">
          <img className="mob-navimg moba" src="/img/logo.svg"/>
          <a href="/upload" className="abutton">Create a Post</a>
          <a href="/createcommunity" className="abutton">Create a Community</a>
          </div>
        </div>
        </div>
      );
    }
  }
}

export default Home;
