import React from "react";
import logo from './logo.svg';
import { useFetchWrapper } from "./_helpers";


function Comment(item) {

  function displayRep(){
    document.getElementById(item.id+"cmrp").style.display = "block";
  }
  function unDisp(){
    document.getElementById(item.id+"cmrp").style.display = "none";
  }
    return (
      <div className="comm" key={item.id}>
        <div className="commtop">
          <a href={"/profile/"+item.author}><img className="post-profile-pic" src={process.env.REACT_APP_API_URL +item.authorpic}></img></a>
          <div className="authorName">{item.author}</div>

        </div>
        <div className="commLine">
        <div className="commcontent">
        {item.content}
        </div>
        <div className="inputs">
          <input className="cbut likeContent" type="button"></input>
          <input className="cbut likeContent" type="button" value="reply" onClick={e => displayRep()}></input>
        </div>
        <div className="rep" style={{display:"none"}} id={item.id + "cmrp"}>
          <div className= "makeComment" onBlur={e=> unDisp()}>
              <span className="mcreply textarea" role="textbox" contentEditable></span>
              <div id={item.id + "cmcounter"}></div>
              <div id={item.id + "subm"}></div>
            </div>
        </div>
        <div className="replies">
          {item.replies.map(comment => Comment(comment))}
        </div>
        </div>
      </div>
    );
  }
export default Comment;
